import "../../../styles/emailLogin.css";
import React, { useRef, useState, useEffect} from 'react'
import { Link, useNavigate, useLocation } from 'react-router-dom';
import useInput from '../../../hooks/useInput';
import useToggle from '../../../hooks/useToggle';
import axios from '../../../api/axios';
import Submit from '../../../images/Log In Button'

const LOGIN_URL = '/api/auth';


const EmailLogIn = () => {
    const { setAuth } = useAuth();

    const navigate = useNavigate();
    const location = useLocation();
    const from = location.state?.from?.pathname || "/";

    const userRef = useRef();
    const errRef = useRef();

    const [email, resetEmail, userAttribs] = useInput('user', '');
    const [password, setPassword] = useState('');
    const [errMsg, setErrMsg] = useState('');
    const [check, toggleCheck] = useToggle('persist', false);

    useEffect(() => {
        userRef.current.focus();
    }, [])

    useEffect(() => {
        setErrMsg('');
    }, [])

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post(LOGIN_URL,
                JSON.stringify({ email, password }),
                {
                    headers: { 'Content-Type': 'application/json'},
                    withCredentials: true
                }
            )
            const accessToken = response?.data?.accessToken;
            const roles = response?.data?.roles;
            setAuth({ email, password, roles, accessToken });
            resetEmail();
            setPassword('');
            navigate(from, { replace: true });
        } catch (err) {
            if (!err?.response) {
                setErrMsg('No Server Response');
            } else if (err.response?.status === 400) {
                setErrMsg('Missing Email or Password');
            } else if (err.response?.statis === 401) {
                setErrMsg('Unautorized')
            } else {
                setErrMsg('Login Failed')
            }
            errRef.current.focus();
        }
    }

    return (
        <section className="showcase">
        <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
        <div className="container-emaillogin">
            <div className="header">SIGN IN</div>
            <form >
                <div className="user-details">
                    <div className="user-input">
                        <label className="details">Email Address</label>
                        <input className="field"/>
                    </div>
                    <div className="user-input">
                        <label className="details">Password</label>
                        <input className="field"/>
                    </div>
