import { Link } from "react-router-dom";
import React from "react";
import stories from './stories.js'

const storyCard = ({ stories }) => {

  return (
    <div className="card">
      <Link className="link" to={`/story/${stories._id}`}>
        <span className="title">{stories._story}</span>
        <img src={stories._imageURL} alt="" className="img" />
        <p className="desc">{stories.description}</p>
        <button className="actionButton">Read More</button>
      </Link>
    </div>
  );
};

export default storyCard;
