import "./emailLogin.css"
import React, { Component } from 'react'
import { Link } from "react-router-dom"

class EmailLogIn extends Component {

    constructor() {
      super()

      this.state = {
         emailAddress: "",
         password: "",
         errors: {}
      };
    };

    onChange = e => {
        this.setState({ 
            [e.target.id]: e.target.value
        });
    };

    onSubmit = e => { 
        e.preventDefault();

        const userData = {
            emailAddress: this.state.emailAddress,
            password: this.state.password
        };

        console.log(userData);
    }

  render() {
      const { errors } = this.state;

    return (
      <section className="showcase">
        <div className="container-emailLogin">
          <form>
            <div className="user-details">
                <span className="details">Email Address</span>
                <input className="field" onChange={this.onChange} value={this.state.emailAddress} error={this.state.errors.emailAddress} id="emailAddress" type="text"/>   
            </div>
            <div className="user-input">
                <span className="details">Last Name</span>
                <input className="field" onChange={this.onChange} value={this.state.lastName} error={this.state.errors.password} id="userpassword" type="password"/>
            </div>
          </form>
         </div>
      </section>
    )
  }
}

export default EmailLogIn
