import { 
    GET_ALL_USERS,
    GET_SPEC_USER,
    ADD_NEW_USER,
    DEL_SPEC_USER
} from '../actions/types';

const initialState = {
    items: [],
    item: {}
};

export default function(state = initialState, action) {
    switch (action.type) { 
        case GET_ALL_USERS:
            return {
                ...state,
                items: action.payload
            };
        case GET_SPEC_USER:
            return {
                ...state,
                item: action.payload
            };
        case ADD_NEW_USER:
            return {
                ...state,
                item: action.payload
            };
        case DEL_SPEC_USER:
            return {
                ...state,
                item: action.payload
            }
        default:
            return state
    }
}
