import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import "./signUp.css"
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerUser } from "../../../../actions/authActions";
import classnames from "classnames";
import { compose } from 'redux';

class SignUp extends Component {

    constructor() {
      super();

      this.state = {
         firstName: "",
         lastName: "",
         dateOfBirth: "",
         email: "",
         social: "",
         password: "",
         errors: {}
      };
    }

    componentDidMount() {
        // IF LOGGED IN AND USER NAVIGATES TO REGISTER PAGE, THIS STATEMENT SHOULD REDIRECT TO THE USER PROFILE
        if (this.props.auth.isAuthenticated) {
            this.props.history.push("/userProfile")
        }
    }

    componentWillRecieveProps(nextProps) {
        if (nextProps.errors) {
            this.setState({
                errors: nextProps.errors
            });
        }
    }

    onChange = e => {
        this.setState({
            [e.target.id]: e.target.value
        })
    };

    onSubmit = e => {
        e.preventDefault();

        const newUser = {
         firstName: this.state.firstName,
         lastName: this.state.lastName,
         dateOfBirth: this.state.dateOfBirth,
         email: this.state.email,
         social: this.state.social,
         password: this.state.password
        };
    this.props.registerUser(newUser, this.props.history)
    };
render() {
    return (
        <section className="showcase">
            <div className="container-signup">
                <div className="header">SIGN UP</div>
                <form noValidate onSubmit={this.onSubmit}>
                    <div className="user-details">
                        <div className="user-input">
                            <label className="details">First Name</label>
                            <span className="red-text">{this.state.errors.firstName}</span>
                            <input className={classnames("field", {invalid: this.state.errors.firstName})} onChange={this.onChange} value={this.state.firstName} error={this.state.errors.firstName} id="firstName" type="text"/>
                        </div>
                        <div className="user-input">
                            <label className="details">Last Name</label>
                            <span className="red-text">{this.state.errors.lastName}</span>
                            <input className={classnames("field", {invalid: this.state.errors.lastName})} onChange={this.onChange} value={this.state.lastName} error={this.state.errors.lastName} id="lastName" type="text"/>
                        </div>
                        <div className="user-input">
                            <label className="details">Date of Birth</label>
                            <span className="red-text">{this.state.errors.dateOfBirth}</span>
                            <input className={classnames("field", {invalid: this.state.errors.dateOfBirth})} onChange={this.onChange} value={this.state.dateOfBirth} error={this.state.errors.dateOfBirth} id="dateOfBirth" type="date"/>
                        </div>
                        <div className="user-input">
                            <label className="details">Email Address</label>
                            <span className="red-text">{this.state.errors.email}</span>
                            <input className={classnames("field", {invalid: this.state.errors.email})} onChange={this.onChange} value={this.state.email} error={this.state.errors.email} id="email" type="text"/>
                        </div>
                        <div className="user-input">
                            <label className="details">Social Media (@)</label>
                            <span className="red-text">{this.state.errors.social}</span>
                            <input className={classnames("field", {invalid: this.state.errors.social})} onChange={this.onChange} value={this.state.social} error={this.state.errors.social} id="social" type="text"/>
                        </div>
                        <div className="user-input">
                            <label className="details">Password</label>
                            <span className="red-text">{this.state.errors.password}</span>
                            <input className={classnames("field", {invalid: this.state.errors.password})} onChange={this.onChange} value={this.state.password} error={this.state.errors.password} id="password" type="password"/>
                        </div>

                    </div>
                    <button className="smButton" src={require('./assets/layout/Join Button.png')} alt="" onClick={registerUser}></button>
                </form>
                </div>    
        </section>

    )
    }
}

SignUp.propTypes = {
    registerUser: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    auth: state.auth,
    errors: state.errors
});

export default compose(connect(
    mapStateToProps, { registerUser }
))(SignUp);
