import { createStore, action, thunk, computed } from 'easy-peasy';
import stories from './api/stories';
import users from './api/users';

export default createStore({

// STORIES' STATES

    stories: [],
    setStories: action((state, payload) => {
        state.stories = payload;
    }),
    storyTitle: '',
    setStoryTile: action((state, payload) => {
        state.storyTitle = payload;
    }),
    storyURL: '',
    setStoryURL: action((state, payload) => {
        state.storyURL = payload;
    }),
    imageURL: '',
    setImageURL: action((state, payload) => {
        state.imageURL = payload;
    }),
    author: '',
    setAuthor: action((state, payload) => {
        state.author = payload;
    }),
    category: '',
    setCategory: action((state, payload) => {
        state.category = payload;
    }),
    desc: '',
    setDesc: action((state, payload) => {
        state.desc = payload;
    }),
    genre: '',
    setGenre: action((state, payload) => {
        state.genre = payload;
    }),
    regId: 0,
    setRegId: action((state, payload) => {
        state.regId = payload;
    }),
    storyCount: computed((state) => state.stories.length),
    getStoryById: computed((state) => {
        return (id) => state.stories.find(story => (story.id).toString() === id)
    }),
    saveStory: thunk(async (actions, newStory, helpers) => {
        const { stories } = helpers.getState();
        try { 
            const response = await stories.post('/stories', newStory);
            actions.setStories([...stories, response.data]);
            actions.setStoryTitle('')
            actions.setStoryURL('')
            actions.setImageURL('')
            actions.setAuthor('')
            actions.setCategory('')
            actions.setDesc('')
            actions.setGenre('')
            actions.setRegId(0)
        } catch (err) {
            console.log(`Error: ${err.message}`);
        }
    }),
    deleteStory: thunk(async (actions, id, helpers) => {
        const { stories } = helpers.getState();
        try {
            await stories.delete(`/stories/${id}`);
            actions.setStories(stories.filter(story => story.id !== id));
        } catch (err) {
            console.log(`Error: ${err.message}`);
        }
    }),
    editStory: thunk(async (actions, updatedStory, helpers) => {
        const { stories } = helpers.getState();
        const { id } = updatedStory;
        try {
            await stories.delete(`/stories/${id}`)
            actions.setStories(stories.map(story => story.id === id ? { ...response.data } : story));
        } catch (err) {
            console.log(`Error: ${err.message}`)
        }
    }),

// USERS' STATES

    users: [],
    setUsers: action((state, payload) => {
        state.users = payload;
    }),
    firstName: '',
    setFirstName: action((state, payload) => {
        state.firstName = payload;
    }),
    lastName: '',
    setLastName: action((state, payload) => {
        state.lastName = payload;
    }),
    email: '',
    setEmail: action((state, payload) => {
        state.email = payload;
    }),
    username: '',
    setUsername: action((state, payload) => {
        state.username = payload;
    })
});
