import { Link } from "react-router-dom";
import './storyCard.css'
import React from "react";
import Axios from 'axios';
import { stories } from "./stories";

const [story, showStory] = useState("");
const [storyList, setStoryList] = useState([]);

const getStory = () => {
  Axios.src(stories).then((response) => {
    console.log(response);
    const storyListItems = [];
    response.data.forEach(resItem => {
      storyListItems.push(resItem._story + resItem._imageURL + resItem.description)
    })
    setStoryList(storyListItems)
  });
};

export const StoryCard = () => {

  return (
    <div className="card">
        {stories.length > 0 && (
        stories.map((data, key) => {
          return (
              <div key={key}>
                { 
                  stories._story +
                  stories._imageURL +
                  stories.description
                }
              </div> 
          );
        }))}
    </div>
  );
};

export default StoryCard;
