import "./emailLogin.css"
import React, { Component } from 'react'
import { Link } from "react-router-dom"

class EmailLogIn extends Component {

    constructor() {
      super()

      this.state = {
         emailAddress: "",
         password: "",
         errors: {}
      };
    };

    onChange = e => {
        this.setState({ 
            [e.target.id]: e.target.value
        });
    };

    onSubmit = e => { 
        e.preventDefault();

        const userData = {
            emailAddress: this.state.emailAddress,
            password: this.state.password
        };

        console.log(userData);
    }

  render() {
      const { errors } = this.state;

    return (
        <section className="showcase">
        <div className="container-emaillogin">
            <div className="header">SIGN IN</div>
            <form noValidate onSubmit={this.onSubmit}>
                <div className="user-details">
                    <div className="user-input">
                        <span className="details">Email Address</span>
                        <input className="field" onChange={this.onChange} value={this.state.firstName} error={this.state.errors.firstName} id="firstName" type="text"/>
                    </div>
                    <div className="user-input">
                        <span className="details">Password</span>
                        <input className="field" onChange={this.onChange} value={this.state.password} error={this.state.errors.password} id="password" type="password"/>
                    </div>
                </div>
                <img className="smButton" src={require('./assets/layout/Social Button.png')} alt=""></img>
            </form>
            </div>    
    </section>
    )
  }
}

export default EmailLogIn
