import "./emailLogin.css";
import React, { Component } from 'react';
// import { Link } from "react-router-dom";
// import PropTypes from "prop-types";
// import { connect } from "react-redux";
import classnames from "classnames";

class EmailLogIn extends Component {

    constructor() {
      super()

      this.state = {
         email: "",
         password: "",
         errors: {}
      };

    };

    componentDidMount() {
        // IF LOGGED IN AND USER NAVIGATES TO REGISTER PAGE, THIS STATEMENT SHOULD REDIRECT TO THE USER PROFILE
        if (this.props.auth.isAuthenticated) {
            this.props.history.push("/userProfile")
        }
    }

    componentWillReceiveProps(nextProps) {
        if (nextProps.auth.isAuthenticated) {
            this.props.history.push("/userProfile");
        }

        if (nextProps.errors) {
            this.state({
                errors: nextProps.erors
            })
        }
}

    onChange = e => {
        this.setState({ 
            [e.target.id]: e.target.value
        });
    };

    onSubmit = e => { 
        e.preventDefault();

        const userData = {
            email: this.state.email,
            password: this.state.password
        };

        this.props.loginUser(userData);
    }

  render() {
      const { errors } = this.state;

    return (
        <section className="showcase">
        <div className="container-emaillogin">
            <div className="header">SIGN IN</div>
            <form noValidate onSubmit={this.onSubmit}>
                <div className="user-details">
                    <div className="user-input">
                        <label className="details">Email Address</label>
                        <span className="red-text">{errors.email}{errors.emailNotFound}</span>
                        <input className={classnames("field", {invalid: errors.email || errors.emailNotFound})} onChange={this.onChange} value={this.state.email} error={this.state.errors.email} id="email" type="text"/>
                    </div>
                    <div className="user-input">
                        <label className="details">Password</label>
                        <span className="red-text">{errors.password}{errors.passwordincorrect}</span>
                        <input className={classnames("field", {invalid: errors.password || errors.passwordincorrect})} onChange={this.onChange} value={this.state.password} error={this.state.errors.password} id="password" type="password"/>
                    </div>
                </div>
                <button className="smButton" src={require('./assets/layout/Social Button.png')} type="submit" alt=""></button>
            </form>
            </div>    
    </section>
    )
  }
}

export default EmailLogIn;
