import "./App.css"
import { Route, Switch } from 'react-router-dom';
import { useEffect } from 'react';
import useAxiosFetch from './hooks/useAxiosFetch';
import { useStoreActions } from 'easy-peasy';

import Navbar from './components/box/Navbar/Navbar';
import Header from './components/box/Header/Header';
import Footer from './components/box/Footer/Footer';
import Home from './components/pages/gen/Landing Page/Home'
import SignIn from './components/pages/auth/Sign In Page/SignIn';
import SignUp from './components/pages/auth/Sign Up Page/SignUp';
import EmailLogIn from './components/pages/auth/Email Log In Page/EmailLogIn';
import UserProfile from './components/pages/suite/User Profile Page/UserProfile';
import DataHub from './components/pages/suite/Data Management Page/DataHub'
import Error from './components/pages/gen/Error Page/error';

function App() {

    // CODE TO LOAD DATA

    const setStories = useStoreActions((actions) => actions.setStories);
    const setUsers = useStoreActions((actions) => actions.setUsers);
    const { storyData, storyFetchError, storyIsLoading } = useAxiosFetch('http://localhost:5000/stories');
    const { userData, userFetchError, userIsLoading } = useAxiosFetch('http://localhost:5000/users');

    useEffect(() => {
        setStories(storyData);
    }, [storyData, setStories]);
    useEffect(() => {
        setUsers(userData);
    }, [userData, setUsers])

    return (
        <div>
            <Header />
            <Navbar />
                <Switch>
                   <Route path="/" element={<Home />} /> 
                   <Route path="/signIn" element={<SignIn />} />
                   <Route path="/signUp" element={<SignUp />} />
                   <Route path="/emailLogIn" element={<EmailLogIn />} />
                   <Route path="/signUp" element={<SignUp />} />
                   <Route exact path="/userProfile">
                        <UserProfile 
                            userIsLoading={userIsLoading}
                            userFetchError={userFetchError}
                            storyIsLoading={storyIsLoading}
                            storyFetchError={storyFetchError}
                            /> 
                    </Route>
                   <Route path='/dataHub' element={<DataHub />} />
                   <Route path="*" element={<Error />} />
                </Switch>
            <Footer />
        </div>
    )
    };

export default App;
