import React, { Component } from 'react';
import "./App.css"
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import jwt_decode from 'jwt-decode';
import setAuthToken from './utils/setAuthToken';
import { setCurrentUser, logoutUser } from './actions/authActions';

import { Provider } from 'react-redux'
import store from './store'

import PrivateRoute from './components/private-route/PrivateRoute'

import Navbar from './components/Navbar/Navbar';
import Home from './components/pages/auth/Landing Page/Home'
import SignIn from './components/pages/auth/Sign In Page/SignIn';
import SignUp from './components/pages/auth/Sign Up Page/SignUp';
import EmailLogIn from './components/pages/auth/Email Log In Page/EmailLogIn';
import UserProfile from './components/pages/suite/User Profile Page/UserProfile';
import DataHub from './components/pages/suite/Data Management Page/DataHub'
import Error from './components/pages/Error Page/error';

// CHECK TOKEN TO KEEP USER LOGGED IN
if (localStorage.jwtToken) {
    // SET AUTH TOKEN HEADER AUTH
    const token = localStorage.jwtToken;
    setAuthToken(token);
    // DECODE TOKEN AND GET USER INFO AND EXPIRATION
    const decoded = jwt_decode(token);
    // SET THE USER AND isAuthenticated
    store.dispatch(setCurrentUser(decoded));

// CHECK FOR EXPIRED TOKEN
    const currentTime = Date.now() / 1000
    if (decoded.exp < currentTime) {
    // LOGOUT USER
        store.dispatch(logoutUser());

    // REDIRECT TO LOGIN
        window.location.href = "./signIn";
    }    
}

class App extends Component {
    render() {
    return (
        <Provider store={store}>
        <Router>
            <Navbar>

            </Navbar>
                <Routes>
                   <Route path="/" element={<Home />} /> 
                   <Route path="/signIn" element={<SignIn />} />
                   <Route path="/signUp" element={<SignUp />} />
                   <Route path="/emailLogIn" element={<EmailLogIn />} />
                   <Route path="/signUp" element={<SignUp />} />
                   <Route exact path="/userProfile" component={UserProfile} />
                   <Route path='/dataHub' element={<DataHub />} />
                   <Route path="*" element={<Error />} />
                </Routes>
        </Router>
        </Provider>
    )
    };
};

export default App;
