import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import "./signUp.css"

class SignUp extends Component {

    constructor() {
      super();

      this.state = {
         firstName: "",
         lastName: "",
         dateOfBirth: "",
         emailAddress: "",
         socialMedia: "",
         password: "",
         errors: {}
      };

      this.onChange = this.onChange.bind(this);
      this.onSubmit = this.onSubmit.bind(this); 
    }

    onChange = e => {
        this.setState({
            [e.target.id]: e.target.value
        })
    };

    onSubmit = e => {
        e.preventDefault();

        const newUser = {
         firstName: this.state.firstName,
         lastName: this.state.lastName,
         dateOfBirth: this.state.dateOfBirth,
         emailAddress: this.state.emailAddress,
         socialMedia: this.state.socialMedia,
         password: this.state.password
        };
    console.log(newUser);
    };
render() {
    return (
        <section className="showcase">
            <div className="container-signup">
                <div className="header">SIGN UP</div>
                <form noValidate onSubmit={this.onSubmit}>
                    <div className="user-details">
                        <div className="user-input">
                            <span className="details">First Name</span>
                            <input className="field" onChange={this.onChange} value={this.state.firstName} error={this.state.errors.firstName} id="firstName" type="text"/>
                        </div>
                        <div className="user-input">
                            <span className="details">Last Name</span>
                            <input className="field" onChange={this.onChange} value={this.state.lastName} error={this.state.errors.lastName} id="lastName" type="text"/>
                        </div>
                        <div className="user-input">
                            <span className="details">Date of Birth</span>
                            <input className="field" onChange={this.onChange} value={this.state.dateOfBirth} error={this.state.errors.dateOfBirth} id="dateOfBirth" type="date"/>
                        </div>
                        <div className="user-input">
                            <span className="details">Email address</span>
                            <input className="field" onChange={this.onChange} value={this.state.emailAddress} error={this.state.errors.emailAddress} id="emailAddress" type="text"/>
                        </div>
                        <div className="user-input">
                            <span className="details">Social Media (@)</span>
                            <input className="field" onChange={this.onChange} value={this.state.socialMedia} error={this.state.errors.socialMedia} id="socialMedia" type="text"/>
                        </div>
                        <div className="user-input">
                            <span className="details">Password</span>
                            <input className="field" onChange={this.onChange} value={this.state.password} error={this.state.errors.password} id="password" type="password"/>
                        </div>

                    </div>
                    <button className="smButton" src={require('./assets/layout/Join Button.png')} alt=""></button>
                </form>
                </div>    
        </section>

    )
    }
}

export default SignUp;
